import {Subject, takeUntil} from "rxjs";
import {Component, OnDestroy, OnInit} from "@angular/core";
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {HttpClient} from "@angular/common/http";
import {Router} from "@angular/router";

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],
})
export class RegisterComponent implements OnInit, OnDestroy {
  registerForm!: FormGroup;
  private destroy$ = new Subject<void>();

  constructor(private fb: FormBuilder, private http: HttpClient, private router: Router) {}

  ngOnInit(): void {
    this.initRegisterForm();
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  initRegisterForm(): void {
    this.registerForm = this.fb.group({
      name: ['', [Validators.required]],
      password: [
        '',
        [
          Validators.required,
          Validators.pattern(/^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/),
        ],
      ],
    });
  }

  onSubmit(): void {
    if (this.registerForm!.valid) {
      const { name, password } = this.registerForm!.value;
      this.http
        .post('https://localhost:7009/Auth/Register', { name, password })
        .pipe(takeUntil(this.destroy$))
        .subscribe(() => {
          this.router.navigate(['/login']);
        });
    }
  }
}
<h2>Register</h2>
<form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
  <div>
    <label for="name">Name:</label>
    <input id="name" type="text" formControlName="name" />
    <div *ngIf="registerForm.get('name').invalid && registerForm.get('name').touched">
      Name is required.
    </div>
  </div>
  <div>
    <label for="password">Password:</label>
    <input id="password" type="password" formControlName="password" />
    <div *ngIf="registerForm.get('password').invalid && registerForm.get('password').touched">
      Password must be at least 8 characters long, with at least one uppercase letter, one digit, and one special character.
    </div>
  </div>
  <button type="submit" [disabled]="registerForm.invalid">Register</button>
</form>
