import {Subject, takeUntil} from "rxjs";
import {Component, OnDestroy, OnInit} from "@angular/core";
import {HttpClient} from "@angular/common/http";
import {Router} from "@angular/router";
import {NgForm} from "@angular/forms";

interface ShortUrl {
  urlId: number;
  sourceUrl: string;
  shortUrl: string;
  createdAt: string;
  ownerUser: {
    name: string;
  };
}

interface OwnerUser {
  name: string;
}


@Component({
  selector: 'app-short-urls',
  templateUrl: './short-urls.component.html',
  styleUrls: ['./short-urls.component.css'],
})
export class ShortUrlsComponent implements OnInit, OnDestroy {
  shortUrls: ShortUrl[] = [];
  newUrl = '';
  errorMessage = '';
  userRole: string | null = '';
  userName: string | null = '';
  private destroy$ = new Subject<void>();

  constructor(private http: HttpClient, private router: Router) {}

  ngOnInit(): void {
    this.loadShortUrls();
    this.userRole = localStorage.getItem('userRole');
    this.userName = localStorage.getItem('userName');
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  loadShortUrls(): void {
    this.http
      .get<ShortUrl[]>('https://localhost:7009/api/short-urls', { withCredentials: true })
      .pipe(takeUntil(this.destroy$))
      .subscribe((shortUrls) => {
        this.shortUrls = shortUrls;
      });
  }

  addNewUrl(addUrlForm: NgForm) {
    if (addUrlForm.valid) {
      if (this.newUrl) {
        this.http
          .post<{ id: number }>('https://localhost:7009/api/short-urls', { url: this.newUrl }, { withCredentials: true })
          .pipe(takeUntil(this.destroy$))
          .subscribe(
            (response) => {
              this.loadShortUrls();
              this.newUrl = '';
              this.errorMessage = '';
            },
            (error) => {
              // Handle error, e.g., set error message
              this.errorMessage = 'URL already exists.';
            }
          );
      }
    } else {
      console.error('Invalid URL format.');
    }
  }

  deleteUrl(id: number): void {
    const endpoint = this.userRole === 'Admin'
      ? `https://localhost:7009/api/short-urls/${id}`
      : `https://localhost:7009/api/me/short-urls/${id}`;

    this.http.delete(endpoint, { withCredentials: true })
      .pipe(takeUntil(this.destroy$))
      .subscribe(() => {
        this.loadShortUrls();
      });
  }

  viewDetails(id: number): void {
    this.router.navigate([`/short-url-info/${id}`]);
  }

  redirectToOriginalUrl(shortUrl: string) {
    window.location.href = shortUrl;
  }
}
<h2>Short URLs Table View</h2>
<div *ngIf="errorMessage" class="error-message">{{ errorMessage }}</div>
<table>
  <thead>
  <tr>
    <th>ID</th>
    <th>Source URL</th>
    <th>Short URL</th>
    <th>Created At</th>
    <th>Owner User</th>
    <th>Actions</th>
  </tr>
  </thead>
  <tbody>
  <tr *ngFor="let shortUrl of shortUrls">
    <td>{{ shortUrl.urlId }}</td>
    <td>{{ shortUrl.sourceUrl }}</td>
    <td>{{ shortUrl.shortUrl }}</td>
    <td>{{ shortUrl.createdAt }}</td>
    <td>{{ shortUrl.ownerUser.name }}</td>
    <td>
      <button (click)="redirectToOriginalUrl(shortUrl.shortUrl)">Redirect</button>
      <button (click)="viewDetails(shortUrl.urlId)">
        View Details
      </button>
      <!-- Replace "true" with a condition to check if the user is authorized -->
      <button *ngIf="userRole === 'Admin' || (userRole === 'User' && shortUrl.ownerUser.name === userName)"
              (click)="deleteUrl(shortUrl.urlId)">
        Delete
      </button>
    </td>
  </tr>
  </tbody>
</table>

<!-- Replace "true" with a condition to check if the user is authorized -->
<form #addUrlForm="ngForm" (ngSubmit)="addNewUrl(addUrlForm)">
  <input type="text" name="url" [(ngModel)]="newUrl" #urlInput="ngModel" required pattern="https?://.+">
  <div *ngIf="urlInput.invalid && (urlInput.dirty || urlInput.touched)" class="error">
    <div *ngIf="urlInput.errors?.['required']">
      URL is required.
    </div>
    <div *ngIf="urlInput.errors?.['pattern']">
      Invalid URL format.
    </div>
  </div>
  <button type="submit" [disabled]="addUrlForm.invalid">Add new URL</button>
</form>
