import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { ActivatedRoute } from '@angular/router';
import { environment } from 'src/environments/environment';

@Component({
  selector: 'app-short-url-details',
  templateUrl: './short-url-details.component.html',
  styleUrls: ['./short-url-details.component.css']
})
export class ShortUrlDetailsComponent implements OnInit {
  id: number = 0;
  description: string = '';
  userRole: string | null = '';

  constructor(private http: HttpClient, private route: ActivatedRoute) { }

  ngOnInit(): void {
    // Get the ID from the route parameters
    this.id = +(this?.route?.snapshot?.paramMap?.get('id') ?? 0);

    // Check if the user is an admin
    this.userRole = localStorage.getItem('userRole');

    // Load the description from the API
    this.loadDescription();
  }

  loadDescription(): void {
    this.http.get<{ Description: string }>(`${environment.apiUrl}/short-urls/${this.id}/description`).subscribe(
      (response) => {
        this.description = response.Description;
      },
      (error) => {
        console.error('Error:', error);
      }
    );
  }

  updateDescription(): void {
    this.http.put(`${environment.apiUrl}/short-urls/${this.id}/description`, this.description).subscribe(
      () => {
        alert('Description updated successfully');
      },
      (error) => {
        console.error('Error:', error);
      }
    );
  }
}
<h2>Description</h2>
<form (ngSubmit)="updateDescription()">
  <textarea [(ngModel)]="description" name="description" [readonly]="userRole !== 'Admin'"></textarea>
  <button type="submit" *ngIf="userRole === 'Admin'">Update Description</button>
</form>
